\begin{center}\begin{tikzpicture}[scale=0.8, every node/.style={font=\small}]
% Axes
\draw[->] (0,0) -- (25,0) node[right] {$t$ (hours)};
\draw[->] (0,0) -- (0,40) node[above] {$T(t)$ (\degree C)};

% Midline
\draw[dashed, gray] (0,22) -- (24,22) node[right] {\footnotesize $y=22$};

% Grid lines for key points
\foreach \x in {0,6,12,18,24} {
  \draw (\x,0.1) -- (\x,-0.1) node[below] {$\x$};
}

\foreach \y in {10,22,34} {
  \draw (0.1,\y) -- (-0.1,\y) node[left] {$\y$};
}

% Plot T(t) = 12 sin(pi/12 (t-6)) + 22
\draw[domain=0:24, samples=200, smooth, thick, blue] plot (\x,{12*sin(deg((pi/12)*(\x - 6))) + 22});

% Mark max point at t=12
\filldraw (12,34) circle (2pt) node[above right] {$(12,34)$};

\end{tikzpicture}\end{center}
